package JudgeRouteCircle657;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
/**
 * Created by Andrew on 6/15/2018.
 */
public class JudgeRouteCircleTest {
    @Test
    public void TestPass(){
        assertTrue(JudgeRouteCircle.judgeCircle("LR"));
    }

    @Test
    public void TestFail(){
        assertFalse(JudgeRouteCircle.judgeCircle("UU"));
    }

    @Test
    public void TestWhyDoesThisFail(){
        assertTrue(JudgeRouteCircle.judgeCircle(""));
    }

    @Test
    public void TestPassPositionVariables(){
        assertTrue(JudgeRouteCircle.judgeCircleJustTwoInts("LR"));
    }

    @Test
    public void TestFailPositionVariables(){
        assertFalse(JudgeRouteCircle.judgeCircleJustTwoInts("UU"));
    }

    @Test
    public void TestWhyDoesThisFailPositionVariables(){
        assertTrue(JudgeRouteCircle.judgeCircleJustTwoInts(""));
    }
}